import React, { useState, useEffect } from "react";
import Login from "./Login";
import Items from "./Items";
import Cart from "./Cart"; // if you have one
import api from "./api"; // your api helper

export default function App() {
  const [token, setToken] = useState(null);

  useEffect(() => {
    // on mount, read token from localStorage
    const t = localStorage.getItem("token");
    if (t) setToken(t);
  }, []);

  const onLogin = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
  };

  const onLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };

  if (!token) {
    // not authenticated -> show Login screen
    return <Login onLogin={onLogin} />;
  }

  // authenticated -> render main app
  return (
    <div className="app">
      <header style={{ padding: 12 }}>
        <button onClick={onLogout}>Logout</button>
      </header>

      {/* show Items and other components when logged in */}
      <main style={{ padding: 12 }}>
        <Items />
        {/* <Cart /> etc */}
      </main>
    </div>
  );
}
